import React from "react";
import styles from "./loginPage.module.css";

// Alt bileşenlerinizi içe aktarmayı unutmayın
import EmailUsernameInput from "./emailUsernameInput";
import PasswordInput from "./passwordInput";
import ForgotPasswordLink from "./ForgotPasswordLink";
import LoginButton from "./LoginButton";
import FooterLanguageSelector from "./footerLanguageSelector";

const LoginPage: React.FC = () => {
    const handleLogin = () => {
        // Burada login butonuna tıklanınca çalışacak kodlarınızı yazabilirsiniz.
        console.log("Login tıklandı");
    };

    return (
        <div className={styles.container}>
            <div className={styles.card}>
                <h1 className={styles.title}>Welcome</h1>

                {/* Email / Username girişi */}
                <div className={styles.formGroup}>
                    <EmailUsernameInput />
                </div>

                {/* Şifre girişi */}
                <div className={styles.formGroup}>
                    <PasswordInput />
                </div>

                {/* Login Butonu */}
                <div className={styles.formGroup}>
                    <LoginButton onClick={handleLogin}>Login</LoginButton>
                </div>

                {/* Şifremi unuttum linki */}
                <ForgotPasswordLink />

                {/* Aşağıya dil seçici, alt bilgi vb. koyabilirsiniz */}
                <div className={styles.subText}>
                    <FooterLanguageSelector />
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
